jQuery( document ).ready( function( $ ) {

	var pageAddProperty = function() {

		var pap = this;

		pap.init = function() {
			pap.papFunction();
		};

		pap.papFunction = function() {
			jQuery( '.post-type-property .selectit, .check-column, .inline-edit-private, .inline-edit-col-right .inline-edit-group label' ).each(function(){
				var span = jQuery(this).find('span.checkbox-box');
				if ( !span[0] ) {
					jQuery(this).find('input[type="checkbox"]').after('<span class="checkbox-box"></span>');
				}
			});
		};
	};

	var pap = new pageAddProperty();
	pap.init();

	jQuery( document ).ajaxComplete(pap.init);
} );
